import React, {useContext,useEffect} from 'react'
import {GlobalState} from '../../../GlobalState'
import ProductItem from '../utils/productItem/ProductItem'
import Loading from '../utils/loading/Loading'
//import NotFound from '../utils/not_found/NotFound'



function Products() {

    const state = useContext(GlobalState)
    const [products, setProducts] = state.productsAPI.products
   const [isAdmin] =state.userAPI.isAdmin
    const [token] =state.token
    const [callback, setCallback] =state.productsAPI.callback

    const handleCheck = (id) =>{
       console.log(id)
    }
    const deleteProduct = async() =>{
        try {
            setLoading(true)
            const destroyImg = axios.post('/api/destroy', {public_id: product.images.public_id},{
                headers: {Authorization: token}
            })
            const deleteProduct = axios.delete(`/api/products/${product._id}`, {
                 headers: {Authorization: token}
         })
 
         await destroyImg
         await deleteProduct
         setLoading(false)
         setCallback(!callback)
 
        } catch (err) {
            alert(err.response.data.msg)
        }
     }
 
    
    return ( 
        <> 
        <div className="products">
            {
                products.map(product =>{
                    return <ProductItem key={product._id} product={product} setProduct={setProducts}
                    isAdmin = {isAdmin} token = {token} callback={callback} setCallback={setCallback}/>
                })
            }
        </div>  
        {products.length === 0 && <Loading />}
    {/*  { <NotFound />*/}
        
        </>
    )
}

export default Products
