//rfce
import React, {useState, useContext} from 'react'
import {GlobalState} from '../../GlobalState'
import Menu from './icon/menu.svg'
import Close from './icon/close.svg'
import Heart from './icon/heart1.svg'
import {Link} from 'react-router-dom'


function Header() {
const state = useContext(GlobalState)
const [isLogged, setIsLogged ] = state.userAPI.isLogged
const [iaAdmin, setIsAdmin] = state.userAPI.isAdmin
//console.log(state)

const adminRouter =() =>{
    return(
        <>
        <li><Link to="/create_product">Create Advertsement</Link></li>
        <li><Link to="/category">Genre</Link></li>

        </>
    )
}
const loggedRouter = () =>{
    return(
        <>
        <li><Link to="/history">Create Advertsement</Link></li>
        <li><Link to="/">Logout</Link></li>

        </>
    )

    
}
    return (
        <header>
            <div className ="menu">
                <img src={Menu} alt="" width="30"/>
        </div> 
        <div className="logo">
            <h1>
                <Link to="/">{isAdmin ? 'admin' : 'FLICKPLIX'}</Link>

            </h1>
          </div>
          <ul>
        <li><Link to ="/">{isAdmin ? 'Prodcuts' : 'Advertisments'}</Link></li>
        {isAdmin && adminRouter}
        { 
         <li><Link to ="/login">Login ✥ Register</Link></li>}
        <li><Link to ="/login">Login ✥ Register</Link></li>
       <li>
       <img src={Close} alt="" width="30" className = "menu"/>
       </li>
        </ul>

        <div className ="cart-icon">
            <span>0</span>
            <Link to ="/cart">
            <img src={Heart} alt="" width="30"/>
            </Link>
       
        </div>
      </header>
    )
}

export default Header
