//rfce
import React, {useState, useContext} from 'react'
import {GlobalState} from '../../GlobalState'
import Menu from './icon/menu.svg'
import Close from './icon/close.svg'
import Heart from './icon/heart1.svg'
import {Link} from 'react-router-dom'
import axios from 'axios'



function Header() {
const state = useContext(GlobalState)
const [isLogged, setIsLogged ] = state.userAPI.isLogged
const [isAdmin, setIsAdmin] = state.userAPI.isAdmin
//console.log(state)

const logoutUser = async () =>{
    await axios.get('/user/logout')
    localStorage.clear()
    
}
const adminRouter =() =>{
    return(
        <>
        <li><Link to="/create_product">Create Advertsement</Link></li>
        <li><Link to="/category">Genre</Link></li>

        </>
    )
}
const loggedRouter = () =>{
    return(
        <>
        <li><Link to="/history">History</Link></li>
        <li><Link to="/" onClick= {logoutUser} >Logout</Link></li>

        </>
    )

    
}
    return (
        <header>
            <div className ="menu">
                <img src={Menu} alt="" width="30"/>
        </div> 
        <div className="logo">
            <h1>
                <Link to="/">{isAdmin ? 'Admin' : 'FLICKPLIX'}</Link>

            </h1>
          </div>
          <ul>
        <li><Link to ="/">{isAdmin ? 'Prodcuts' : 'Advertisements'}</Link></li>
        {isAdmin && adminRouter()}
        { 
          isLogged ? loggedRouter() :<li><Link to ="/login">Login ✥ Register</Link></li>
         }
       
       <li>
             <img src={Close} alt="" width="30" className = "menu"/>
       </li>
        </ul>
         {
             isAdmin ? ''
             :<div className ="cart-icon">
            <span>0</span>
            <Link to ="/cart">
            <img src={Heart} alt="" width="30"/>
            </Link>
        </div>
        }
      </header>
    )
}

export default Header
